mapper.select:
select count(*)
from letter
where


		rnick = #{rnick}


		snick = #{snick}

select lnum, rnick, snick, title, content, DATE_FORMAT(datetime, '%Y-%m-%d') as date, ischeck
from letter
where


		rnick = #{rnick}


		snick = #{snick}


order by datetime desc


		limit #{rstart},


		limit #{sstart},


#{count}

select lnum, rnick, snick, title, content, DATE_FORMAT(datetime, '%Y-%m-%d') as date, ischeck
from letter
where lnum = #{lnum}
